function burgerMenu(){const e=document.querySelector(".burger"),s=document.querySelector(".menu"),t=document.querySelector("body"),i=document.querySelectorAll(".menu__item");e.addEventListener("click",(()=>{s.classList.contains("active")?(s.classList.remove("active"),e.classList.remove("active-burger"),t.classList.remove("locked")):(s.classList.add("active"),e.classList.add("active-burger"),t.classList.add("locked"))})),i.forEach((i=>{i.addEventListener("click",(()=>{e.classList.toggle("active-burger"),s.classList.toggle("active"),t.classList.remove("locked")}))})),window.addEventListener("resize",(()=>{window.innerWidth>991.98&&(s.classList.remove("active"),e.classList.remove("active-burger"),t.classList.remove("locked"))}))}function fixedNav(){const e=document.querySelector("nav");window.scrollY>=1?e.classList.add("fixed__nav"):e.classList.remove("fixed__nav")}burgerMenu(),window.addEventListener("scroll",fixedNav);const swiper=new Swiper(".mySwiper",{slidesPerView:3,spaceBetween:30,loop:!0,loopFillGroupWithBlank:!0,pagination:!1,navigation:{nextEl:".swiper-button-next",prevEl:".swiper-button-prev"},breakpoints:{320:{slidesPerView:1,spaceBetween:20},640:{slidesPerView:2,spaceBetween:30},1e3:{slidesPerView:3,spaceBetween:30}}});